import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.css";
import API from "../constants";
import StarRatingComponent from 'react-star-rating-component';

class Listings extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listings: []
    };
  }

  componentDidMount() {
    fetch(API + "/get_listings")
      .then(response => response.json())
      .then(data => this.setState({ listings: data.listings }))
      .catch(error => console.error(error));
  }

  sortByItemRatings = () => {
    listings
  }
  sortBySellerRatings = () => {

  }
  sortByItem = () => {

  }
  sortBySeller = () => {

  }

  //homes.sort((a, b) => parseFloat(a.price) - parseFloat(b.price));

  render() {
    const { listings } = this.state;

    return (
      <div className="container">
        <h3>Listings</h3>
        <h5>(what sellers are currently selling in the marketplace)</h5>
        <table className="table table-bordered table-hover table-condensed">
          <thead>
            <tr>
              <th>Item ID</th>
              <th>Item Name</th>
              <th>Price</th>
              <th>Quantity</th>
              <th>Seller Name</th>
              <th>Item Rating</th>
              <th>Seller Rating</th>
            </tr>
          </thead>
          <tbody>
            {listings.map(listing => (
              <tr>
                <td>{listing.item_id}</td>
                <td>{listing.item_name}</td>
                <td>${(listing.price / 100).toFixed(2)}</td>
                <td>{listing.quantity}</td>
                <td>{listing.seller_name}</td>
                <td>
                  <StarRatingComponent 
                    name="item_rating"
                    editing={false}
                    starCount={5}
                    value={listing.item_rating}
                  />
                </td>
                <td>
                  <StarRatingComponent 
                    name="seller_rating"
                    editing={false}
                    starCount={5}
                    value={listing.seller_rating}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Listings;
